//SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

interface IPIX {
    event TraderUpdated(address indexed trader, bool approved);

    event ModeratorUpdated(address indexed moderator, bool approved);

    event PackPriceUpdated(uint256 indexed mode, uint256 price);

    event CombinePriceUpdated(uint256 price);

    event PaymentTokenUpdated(address indexed token, bool approved);

    event TreasuryUpdated(address treasury, uint256 fee);

    event PIXMinted(
        address indexed account,
        uint256 indexed tokenId,
        uint256 indexed pixId,
        PIXCategory category,
        PIXSize size
    );

    event Combined(uint256 indexed tokenId, PIXCategory category, PIXSize size);

    event Requested(
        uint256 indexed dropId,
        uint256 indexed playerId,
        uint256 indexed mode,
        uint256 purchasedPacks,
        uint256 count
    );

    enum PIXCategory {
        Legendary,
        Rare,
        Uncommon,
        Common,
        Outliers
    }

    enum PIXSize {
        Pix,
        Area,
        Sector,
        Zone,
        Domain
    }

    struct Treasury {
        address treasury;
        uint256 fee;
    }

    struct PIXInfo {
        uint256 pixId;
        PIXCategory category;
        PIXSize size;
    }

    struct DropInfo {
        uint256 maxCount;
        uint256 requestCount;
        uint256 limitForPlayer;
        uint256 startTime;
        uint256 endTime;
    }

    struct PackRequest {
        uint256 playerId;
        uint256 dropId;
    }

    function isTerritory(uint256 tokenId) external view returns (bool);

    function pixesInLand(uint256[] calldata tokenIds) external view returns (bool);

    function setPIXInLandStatus(uint256[] calldata pixIds) external;

    function safeMint(address to, PIXInfo memory info) external;

    function lastTokenId() external view returns (uint256);
}

contracts/PIXT.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";
import "@openzeppelin/contracts/utils/cryptography/draft-EIP712.sol";

contract PIXT is ERC20Burnable, EIP712("PlanetIX", "1") {
    bytes32 private constant PERMIT_FOR_BID_HASH =
        keccak256(
            "PermitForBid(address owner,address spender,uint256 amount,address nftToken,uint256 tokenId,uint256 nonce)"
        );

    mapping(address => uint256) public nonces;

    constructor() ERC20("PlanetIX", "IXT") {
        // initial supply : 153,258,228
        _mint(msg.sender, 153258228 * 1e18);
    }

    function permitForBid(
        address owner,
        address spender,
        uint256 amount,
        address nftToken,
        uint256 tokenId,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) external {
        uint256 nonce = nonces[owner]++;
        bytes32 structHash = keccak256(
            abi.encode(PERMIT_FOR_BID_HASH, owner, spender, amount, nftToken, tokenId, nonce)
        );
        bytes32 hash = _hashTypedDataV4(structHash);
        address signer = ECDSA.recover(hash, v, r, s);
        require(signer == owner, "PIXT: INVALID_SIGNATURE");

        _approve(owner, spender, amount);
    }
}

